package org.tns_operators;

public class bitwise_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=15,y=3;
		System.out.println(x&y);
		System.out.println(x|y);
		System.out.println(x<<y);
		System.out.println(x>>y);
		System.out.println(x^y);
		

	}

}
